local function ShowVerificationUI(isSuccess)local verificationInterface=Instance.new("ScreenGui")verificationInterface.Name="WhitelistVerificationSystem"verificationInterface.Parent=game.CoreGui verificationInterface.ZIndexBehavior=Enum.ZIndexBehavior.Sibling local mainContainer=Instance.new("Frame")mainContainer.Size=UDim2.new(0,400,0,200)mainContainer.Position=UDim2.new(0.5,-200,0.5,-100)mainContainer.BackgroundColor3=Color3.fromRGB(30,30,30)mainContainer.BorderSizePixel=0 mainContainer.BackgroundTransparency=0.1 mainContainer.Parent=verificationInterface local roundedCorners=Instance.new("UICorner")roundedCorners.CornerRadius=UDim.new(0,12)roundedCorners.Parent=mainContainer local borderOutline=Instance.new("UIStroke")borderOutline.Color=isSuccess and Color3.fromRGB(0,255,0)or Color3.fromRGB(255,0,0)borderOutline.Thickness=3 borderOutline.Parent=mainContainer local titleDisplay=Instance.new("TextLabel")titleDisplay.Size=UDim2.new(1,0,0,60)titleDisplay.Position=UDim2.new(0,0,0,0)titleDisplay.BackgroundTransparency=1 titleDisplay.Text=isSuccess and"Verification Successfully Completed"or"Verification Process Failed"titleDisplay.TextColor3=isSuccess and Color3.fromRGB(0,255,0)or Color3.fromRGB(255,0,0)titleDisplay.TextScaled=true titleDisplay.Font=Enum.Font.GothamBold titleDisplay.Parent=mainContainer local messageContent=Instance.new("TextLabel")messageContent.Size=UDim2.new(0.9,0,0,80)messageContent.Position=UDim2.new(0.05,0,0.3,0)messageContent.BackgroundTransparency=1 messageContent.Text=isSuccess and"Welcome to use script!\nUser: "..game.Players.LocalPlayer.Name or"Unauthorized use\nPlease contact author for permission"messageContent.TextColor3=Color3.fromRGB(255,255,255)messageContent.TextScaled=true messageContent.Font=Enum.Font.Gotham messageContent.TextWrapped=true messageContent.Parent=mainContainer local countdownTimer=Instance.new("TextLabel")countdownTimer.Size=UDim2.new(1,0,0,30)countdownTimer.Position=UDim2.new(0,0,0.8,0)countdownTimer.BackgroundTransparency=1 countdownTimer.Text="Auto close in 3 seconds"countdownTimer.TextColor3=Color3.fromRGB(200,200,200)countdownTimer.TextScaled=true countdownTimer.Font=Enum.Font.Gotham countdownTimer.Parent=mainContainer spawn(function()for timerCountdown=3,1,-1 do countdownTimer.Text=timerCountdown.." seconds remaining until auto close"wait(1)end verificationInterface:Destroy()end)return verificationInterface end local function UltimateValidation()local currentPlayerName=game.Players.LocalPlayer.Name local networkRequestSuccess,whitelistCheckResult=pcall(function()local httpResponseContent=game:HttpGet("https://pastebin.com/raw/rN79QBix")local authorizedUsersTable={}for individualUserName in httpResponseContent:gmatch("[^,]+")do authorizedUsersTable[individualUserName:gsub("%s+","")]=true end return authorizedUsersTable[currentPlayerName]==true end)if networkRequestSuccess and whitelistCheckResult then ShowVerificationUI(true)wait(3)return true else ShowVerificationUI(false)wait(3)return false end end if not UltimateValidation()then return end local WindUIInterface=loadstring(game:HttpGet("https://raw.githubusercontent.com/iyoulin/iyoulin/refs/heads/main/ato"))()local scriptStartTime=tick()local virtualUserInstance=game:GetService("VirtualUser")game:GetService("Players").LocalPlayer.Idled:Connect(function()virtualUserInstance:Button2Down(Vector2.new(0,0),workspace.CurrentCamera.CFrame)wait(1)virtualUserInstance:Button2Up(Vector2.new(0,0),workspace.CurrentCamera.CFrame)end)local mainWindowInterface=WindUIInterface:CreateWindow({Title="KolixiAutomationScript",Icon="rbxassetid://139743288604595",Author="YsiaDeveloper",Folder="",Size=UDim2.fromOffset(290,340),Transparent=true,Theme="Dark",SideBarWidth=130,HasOutline=true})mainWindowInterface:EditOpenButton({Title=" Cr4zyAutomation",Icon="image-upscale",CornerRadius=UDim.new(0,10),StrokeThickness=3,Color=ColorSequence.new(Color3.fromHex("FF0F7B"),Color3.fromHex("F89B29"))})local primaryTabSection=mainWindowInterface:Tab({Title="MainFunctions",Icon=""})_G.autoPetSwitchingFirst=false _G.autoPetSwitchingSecond=false _G.autoRebirthProcess=false _G.autoTrainingProcess=false _G.autoWheelSpinning=false function automatedPetSwitchingFunctionOne()while _G.autoPetSwitchingFirst do wait(0.3)pcall(function()local requiredStrengthCalculation=1000+5000*game.Players.LocalPlayer.leaderstats.Rebirths.Value/2 if game.Players.LocalPlayer.leaderstats.Strength.Value>=requiredStrengthCalculation then for _,petInstance in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do if petInstance.Name=="Tribal Overlord"then game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("equipPet",petInstance)end end end end)end end function automatedPetSwitchingFunctionTwo()while _G.autoPetSwitchingSecond do wait(0.3)pcall(function()local requiredStrengthCalculation=1000+5000*game.Players.LocalPlayer.leaderstats.Rebirths.Value/2 if game.Players.LocalPlayer.leaderstats.Strength.Value<requiredStrengthCalculation then for _,petInstance in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do if petInstance.Name=="Swift Samurai"then game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("equipPet",petInstance)end end end end)end end primaryTabSection:Toggle({Title="Automatic Pet Switching System",Value=false,Callback=function(toggleState)_G.autoPetSwitchingFirst=toggleState _G.autoPetSwitchingSecond=toggleState if toggleState then automatedPetSwitchingFunctionOne()automatedPetSwitchingFunctionTwo()end end})function automatedRebirthProcessFunction()while _G.autoRebirthProcess do wait()pcall(function()if tostring(game.Players.LocalPlayer.equippedPets.pet1.Value)=="Tribal Overlord"then game:GetService("ReplicatedStorage").rEvents.rebirthRemote:InvokeServer("rebirthRequest")end end)end end primaryTabSection:Toggle({Title="Automatic Rebirth Process",Value=false,Callback=function(toggleState)_G.autoRebirthProcess=toggleState automatedRebirthProcessFunction()end})function automatedTrainingProcessFunction()while _G.autoTrainingProcess do wait()pcall(function()if tostring(game.Players.LocalPlayer.equippedPets.pet1.Value)=="Swift Samurai"then for trainingIteration=1,10 do game:GetService("Players").LocalPlayer.muscleEvent:FireServer("rep")end end end)end end primaryTabSection:Toggle({Title="Automatic Training Process",Value=false,Callback=function(toggleState)_G.autoTrainingProcess=toggleState automatedTrainingProcessFunction()end})function automatedWheelSpinningFunction()while _G.autoWheelSpinning do wait()pcall(function()local wheelSpinningArguments={[1]="openFortuneWheel",[2]=game:GetService("ReplicatedStorage").fortuneWheelChances:FindFirstChild("Fortune Wheel")}game:GetService("ReplicatedStorage").rEvents.openFortuneWheelRemote:InvokeServer(unpack(wheelSpinningArguments))end)end end primaryTabSection:Toggle({Title="Automatic Wheel Spinning",Value=false,Callback=function(toggleState)_G.autoWheelSpinning=toggleState automatedWheelSpinningFunction()end})function extremeFPSOptimizationProcess()local lightingService,playersService,localPlayerInstance=game:GetService("Lighting"),game:GetService("Players"),game:GetService("Players").LocalPlayer settings().Rendering.QualityLevel,settings().Physics.PhysicsEnvironmentalThrottle=0,3 lightingService.Brightness,lightingService.Ambient,lightingService.OutdoorAmbient,lightingService.GlobalShadows=0.1,Color3.new(0,0,0),Color3.new(0,0,0),false for _,lightingChild in pairs(lightingService:GetChildren())do pcall(function()lightingChild:Destroy()end)end for _,workspaceDescendant in pairs(workspace:GetDescendants())do if workspaceDescendant:IsA("BasePart")then workspaceDescendant.Transparency,workspaceDescendant.CanCollide,workspaceDescendant.CastShadow=1,false,false else pcall(function()workspaceDescendant:Destroy()end)end end for _,otherPlayer in pairs(playersService:GetPlayers())do if otherPlayer~=localPlayerInstance and otherPlayer.Character then pcall(function()otherPlayer.Character:Destroy()end)end end if localPlayerInstance.PlayerGui then for _,guiElement in pairs(localPlayerInstance.PlayerGui:GetDescendants())do pcall(function()guiElement:Destroy()end)end end if localPlayerInstance.Character then for _,characterPart in pairs(localPlayerInstance.Character:GetDescendants())do if characterPart:IsA("BasePart")then characterPart.Transparency=1 end end end print("Extreme FPS Optimization Process Completed Successfully")end primaryTabSection:Button({Title="Extreme FPS Optimization Process",Callback=function()extremeFPSOptimizationProcess()end})
