local function ShowVerificationUI(isSuccess)local screenGui=Instance.new("ScreenGui")screenGui.Name="WhitelistVerification"screenGui.Parent=game.CoreGui screenGui.ZIndexBehavior=Enum.ZIndexBehavior.Sibling local mainFrame=Instance.new("Frame")mainFrame.Size=UDim2.new(0,400,0,200)mainFrame.Position=UDim2.new(0.5,-200,0.5,-100)mainFrame.BackgroundColor3=Color3.fromRGB(30,30,30)mainFrame.BorderSizePixel=0 mainFrame.BackgroundTransparency=0.1 mainFrame.Parent=screenGui local corner=Instance.new("UICorner")corner.CornerRadius=UDim.new(0,12)corner.Parent=mainFrame local stroke=Instance.new("UIStroke")stroke.Color=isSuccess and Color3.fromRGB(0,255,0)or Color3.fromRGB(255,0,0)stroke.Thickness=3 stroke.Parent=mainFrame local title=Instance.new("TextLabel")title.Size=UDim2.new(1,0,0,60)title.Position=UDim2.new(0,0,0,0)title.BackgroundTransparency=1 title.Text=isSuccess and "✅ 成功授权" or "❌ 未获得授权"title.TextColor3=isSuccess and Color3.fromRGB(0,255,0)or Color3.fromRGB(255,0,0)title.TextScaled=true title.Font=Enum.Font.GothamBold title.Parent=mainFrame local message=Instance.new("TextLabel")message.Size=UDim2.new(0.9,0,0,80)message.Position=UDim2.new(0.05,0,0.3,0)message.BackgroundTransparency=1 message.Text=isSuccess and "成功授权！任何问题找68848516\n用户: "..game.Players.LocalPlayer.Name or "未获得授权\n请联系68848516"message.TextColor3=Color3.fromRGB(255,255,255)message.TextScaled=true message.Font=Enum.Font.Gotham message.TextWrapped=true message.Parent=mainFrame local countdown=Instance.new("TextLabel")countdown.Size=UDim2.new(1,0,0,30)countdown.Position=UDim2.new(0,0,0.8,0)countdown.BackgroundTransparency=1 countdown.Text="3秒后自动关闭"countdown.TextColor3=Color3.fromRGB(200,200,200)countdown.TextScaled=true countdown.Font=Enum.Font.Gotham countdown.Parent=mainFrame spawn(function()for i=3,1,-1 do countdown.Text=i.."秒后自动关闭"wait(1)end screenGui:Destroy()end)return screenGui end local function UltimateValidation()local playerName=game.Players.LocalPlayer.Name local success,isWhitelisted=pcall(function()local response=game:HttpGet("https://raw.githubusercontent.com/qiaoxxyxx/Roblox/refs/heads/main/whitelist.txt")local whitelist={}for name in response:gmatch("[^,]+")do whitelist[name:gsub("%s+","")]=true end return whitelist[playerName]==true end)if success and isWhitelisted then ShowVerificationUI(true)wait(3)return true else ShowVerificationUI(false)wait(3)return false end end if not UltimateValidation()then return end local WindUI=loadstring(game:HttpGet("https://raw.githubusercontent.com/iyoulin/iyoulin/refs/heads/main/ato"))()local Start=tick()local vu=game:GetService("VirtualUser")game:GetService("Players").LocalPlayer.Idled:Connect(function()vu:Button2Down(Vector2.new(0,0),workspace.CurrentCamera.CFrame)wait(1)vu:Button2Up(Vector2.new(0,0),workspace.CurrentCamera.CFrame)end)local Window=WindUI:CreateWindow({Title="Kolixi",Icon="rbxassetid://139743288604595",Author="Ysia",Folder="",Size=UDim2.fromOffset(290,340),Transparent=true,Theme="Dark",SideBarWidth=130,HasOutline=true})Window:EditOpenButton({Title="Cr4zy",Icon="image-upscale",CornerRadius=UDim.new(0,10),StrokeThickness=3,Color=ColorSequence.new(Color3.fromHex("FF0F7B"),Color3.fromHex("F89B29"))})local MainTab=Window:Tab({Title="Main",Icon=""})local fi=0 local StatsDisplay=MainTab:Paragraph({Title="Stats:",Desc="",Image="users",ImageSize=25})spawn(function()while wait()do pcall(function()fi=1000+5000*game.Players.LocalPlayer.leaderstats.Rebirths.Value/2 StatsDisplay:SetDesc("Rebirth Required:"..fi.."\nCurrent Pet:"..tostring(game.Players.LocalPlayer.equippedPets.pet1.Value))end)end end)_G.autoqie1=false function autoqie1()while _G.autoqie1 do wait(0.3)for _,v in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do for _,h in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do if game.Players.LocalPlayer.leaderstats.Strength.Value>=fi then if v.Name=="Tribal Overlord"and h.Name=="Swift Samurai"then game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("unequipPet",h)game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("equipPet",v)end end end end end end MainTab:Toggle({Title="Auto Switch 1",Value=true,Callback=function(state)_G.autoqie1=state autoqie1()end})_G.autoqie2=false function autoqie2()while _G.autoqie2 do wait(0.3)for _,v in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do for _,h in pairs(game.Players.LocalPlayer.petsFolder.Unique:GetChildren())do if game.Players.LocalPlayer.leaderstats.Strength.Value<fi then if v.Name=="Tribal Overlord"and h.Name=="Swift Samurai"then game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("unequipPet",v)game:GetService("ReplicatedStorage").rEvents.equipPetEvent:FireServer("equipPet",h)end end end end end end MainTab:Toggle({Title="Auto Switch 2",Value=true,Callback=function(state)_G.autoqie2=state autoqie2()end})_G.autopetreb=false function autopetreb()while _G.autopetreb do wait()if tostring(game.Players.LocalPlayer.equippedPets.pet1.Value)=="Tribal Overlord"then game:GetService("ReplicatedStorage").rEvents.rebirthRemote:InvokeServer("rebirthRequest")end end end MainTab:Toggle({Title="Auto Rebirth",Value=true,Callback=function(state)_G.autopetreb=state autopetreb()end})_G.farmreb=false function farmreb()while _G.farmreb do wait()if tostring(game.Players.LocalPlayer.equippedPets.pet1.Value)=="Swift Samurai"then for _=1,10 do game:GetService("Players").LocalPlayer.muscleEvent:FireServer("rep")end end end end MainTab:Toggle({Title="Auto Train",Desc="",Value=true,Callback=function(state)_G.farmreb=state farmreb()end})_G.auto_zhuanp=true function auto_zhuanp()while _G.auto_zhuanp do wait()local args={[1]="openFortuneWheel",[2]=game:GetService("ReplicatedStorage").fortuneWheelChances:FindFirstChild("Fortune Wheel")}game:GetService("ReplicatedStorage").rEvents.openFortuneWheelRemote:InvokeServer(unpack(args))end end MainTab:Toggle({Title="Auto Wheel",Value=true,Callback=function(state)_G.auto_zhuanp=state auto_zhuanp()end})function extremeFPS()local l,p,lp=game:GetService("Lighting"),game:GetService("Players"),game:GetService("Players").LocalPlayer settings().Rendering.QualityLevel,settings().Physics.PhysicsEnvironmentalThrottle=0,3 l.Brightness,l.Ambient,l.OutdoorAmbient,l.GlobalShadows=0.1,Color3.new(0,0,0),Color3.new(0,0,0),false for _,v in pairs(l:GetChildren())do pcall(function()v:Destroy()end)end for _,v in pairs(workspace:GetDescendants())do if v:IsA("BasePart")then v.Transparency,v.CanCollide,v.CastShadow=1,false,false else pcall(function()v:Destroy()end)end end for _,v in pairs(p:GetPlayers())do if v~=lp and v.Character then pcall(function()v.Character:Destroy()end)end end if lp.PlayerGui then for _,v in pairs(lp.PlayerGui:GetDescendants())do pcall(function()v:Destroy()end)end end if lp.Character then for _,v in pairs(lp.Character:GetDescendants())do if v:IsA("BasePart")then v.Transparency=1 end end end print("Extreme FPS Optimized")end MainTab:Button({Title="Extreme",Callback=function()extremeFPS()end})
